var macro_plastic = []
var macro_Size = 5;


class Plastic{

    constructor(p){
      this.x = p.random(400, p.width);
      this.y = p.random(400, 200);
    }


    show(p) {
      p.noStroke();
      p.fill(p.random(0,255));
      p.ellipse(this.x, this.y, p.random(5,micro_Size), p.random(5, micro_Size));
    }

  }

export function setupPlastic(p,macroGrowth2050)  {

  //
  //set up plastic
  //
    for(var i = 0; i < 200; i++) {
        macro_plastic[i] = new Plastic(p);
    }
  }

