    }

  }



  class Drop{

    constructor(){
      this.x = 0 + 100
      this.y = 0 + 100;
    }


    show() {
      p.noStroke();
      p.fill(255);
      p.ellipse(this.x, this.y, 20, 30);
    }
    update() {
      this.speed = 5;
      this.gravity = 1.05;
      this.y = this.y + this.speed*this.gravity;

