//var color ;


export default function sketch(p) {
    let canvas;
    var temp_array = new Array(p.floor(1600));
    let yvalues;


    p.setup = () => {
        canvas = p.createCanvas(600, 300);
        p.noStroke();
        yvalues = new Array(p.floor(200));
    }

    p.draw = () => {
        //console.log('draw');
        //console.log(this);
        p.background('blue');
        p.calcSun();
        p.drawSun();
        //p.fill(color)
    }
    p.calcSun= () => {

      for (let i = 0; i < (tempArray.length/16); i++) {
        console.log(temp_array);
        yvalues[i] = tempArray[i]+1

      }
    }
    p.drawSun= () => {
      for (let i = 0; i < (yvalues.length); i++) {

        p.ellipse(p.width, 0, yvalues[i]*10, yvalues[i]*10);
        p.fill('yellow')

      }
    }
    p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
      if(canvas){ //Make sure the canvas has been created
        console.log('customProp');
        console.log(newProps.tempArray);
        temp_array = newProps.tempArray.tempData
      }

    }



}
