export default function microPlastic(p) {

  let canvas;

  //these are gonna be the variables for our garbage collection
  let song;
  let particleMoveFactor;
  let fft;
  let particleCount;
  let particleCreateFactor = 8;
  let bgQueue;
  let fgQueue;
  let toggleSound;

  //these are all variables for our wave function
  let xspacing = 16; // Distance between each horizontal location
  let w; // Width of entire wave
  let theta = 0.15; // Start angle at 0
  let amplitude = 25.0; // Height of wave
  let period = 200.0; // How many pixels before the wave repeats
  let dx=2; // Value for incrementing x
  let yvalues; // Using an array to store height values for the wave
  let backvalues;
  let back = 2 // back offset



  p.setup = () => {
    canvas = p.createCanvas(600, 300);
    //set up wave
    w = p.width + 16;
    dx = (2*Math.PI / period) * xspacing;
    yvalues = new Array(p.floor(w / xspacing));
    backvalues = new Array(p.floor(w / xspacing));


    //set up plastic

    //load plastic images into Array


  }
  p.draw = () => {
    p.background('blue');

    //do wave
    p.calcWave();
    p.renderWave();
    p.backWave();
  }
  //
  //
  //
  //hear lies al information on how to create plastic
  //
  //
  //





  //
  //
  //
  //hear lies al information on how to create a wave
  //
  //
  //
  p.calcWave= () =>  {
    // Increment theta (try different values for
    // 'angular velocity' here)
    theta += 0.02;

    // For every x value, calculate a y value with sine function
    let x = theta;
    for (let i = 0; i < yvalues.length; i++) {

      yvalues[i] = Math.sin(x) * amplitude;
      backvalues[i] = Math.sin(x+back) * amplitude;
      x += dx;
    }
  }
  p.renderWave = () => {
    p.noStroke();
    p.fill(37, 207, 122);
    // A simple way to draw the wave with an ellipse at each location
    for (let x = 0; x < yvalues.length; x++) {
      var waveHeight = 4*p.height / 8 + yvalues[x]
      p.ellipse((x) * xspacing, p.height, 64,waveHeight);

    }
  }
  p.backWave = () => {
    p.noStroke();
    p.fill(19, 149, 244);
    // A simple way to draw the wave with an ellipse at each location
    for (let x = 0; x < backvalues.length; x++) {
      var waveHeight = 4*p.height / 8 + backvalues[x]
      p.ellipse((x) * xspacing, p.height, 64,waveHeight);

    }
  }
}
