let canvas;
//these are gonna be the variables for our garbage collection
var drops = []
var microGrowth2050_array;
var micro_Size = 5;
var current_date;

export function setupDrops(p,microGrowth2050)  {

  //
  //set up plastic
  //
    for(var i = 0; i < 200; i++) {
        drops[i] = new Drop();
    }
  }

export function drawDots(p,microGrowth2050) {
  //calc microplastic
  //
  //p.background(230, 230, 250);
  for (var i = 0; i < drops.length; i++) {
    drops[i].update();
    drops[i].show();
  }
  if (microGrowth2050 != null){
    micro_Size = -1*(microGrowth2050_array[(current_date -2000)*5][1]-367)/10
  }



}

class Drop{

  constructor(){
    this.x = p.random(0, p.width);
    this.y = p.random(0,p.height);
  }


  show() {
    p.noStroke();
    p.fill(255);
    p.ellipse(this.x, this.y, p.random(5,micro_Size), p.random(5, micro_Size));
  }
  update() {
    this.speed = this.speed = p.random(2, 4);;
    this.gravity = 1.05;
    this.y = this.y + this.speed*this.gravity;

    if (this.y > p.height) {
      this.y = 250;
      this.gravity = 0;
      }
    }
  }
