//var color ;


export default function sketch(p) {
    let canvas;
    var temp_array = null;
    var yvalues;


    p.setup = () => {
        canvas = p.createCanvas(600, 300);
        p.noStroke();
        yvalues = new Array(p.floor(200));

    }

    p.draw = () => {
        //console.log('draw');
        //console.log(this);
        p.background('blue');
        p.calcSun();
        p.drawSun();
        //p.fill(color)
        //console.log(temp_array);
    }
    p.calcSun= () => {

      //console.log(temp_array)
      if (temp_array!=null){
        for (let i = 0; i < (temp_array.length/8); i++) {
          console.log(temp_array[i*8].land);
          let average = (temp_array[i*8].station+temp_array[i*8].land)/2
          yvalues[i] = average+1
          //console.log(average);
        }
      }
    }
    p.drawSun= () => {
      if (temp_array!=null){
        //console.log('here');
        //console.log(yvalues);
        for (let i = 0; i < (yvalues.length); i++) {
          //console.log(yvalues);
          p.ellipse(p.width, 0, yvalues[i]*10, yvalues[i]*10);
          p.fill('yellow')
        }
      }
    }
    p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
      if(canvas){ //Make sure the canvas has been created
        console.log('customProp');
        //console.log(newProps.tempArray);
        temp_array = newProps.tempArray.tempData
        console.log(newProps.tempArray.tempData);

      }

    }



}
